import React from 'react'
import { Route, Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Container, Navbar, Nav, NavDropdown } from 'react-bootstrap'
import { LinkContainer } from 'react-router-bootstrap'
import { logout } from '../actions/userActions'
import SearchBox from './SearchBox'
// import { Link } from 'react-router-dom'

const Header = () => {
    const dispatch = useDispatch()

    const userLogin = useSelector(state => state.userLogin)
    const { userInfo } = userLogin

    const logoutHandler = () => {
        dispatch(logout())
    }

    return (
        // <header>
        //     <Navbar bg="dark" variant='dark' expand="lg" collapseOnSelect>
        //         <Container>
        //             <LinkContainer to='/'>
        //                 <Navbar.Brand >Thuáº­n's Shoe</Navbar.Brand>
        //             </LinkContainer>

        //             <Navbar.Toggle aria-controls="basic-navbar-nav" />
        //             <Navbar.Collapse id='basic-navbar-nav'>
        //                 <Nav className="ml-auto">
        //                     <NavDropdown title='Brand'>
        //                         <LinkContainer to='/brand/Nike'>
        //                             <NavDropdown.Item > <strong>Nike</strong>  </NavDropdown.Item>

        //                         </LinkContainer>

        //                         <LinkContainer to='/brand/Converse'>
        //                             <NavDropdown.Item><strong>Converse</strong>  </NavDropdown.Item>
        //                         </LinkContainer>
        //                     </NavDropdown>
        //                 </Nav>
        //             </Navbar.Collapse>
        //             <Navbar.Collapse className='justify-content-end' id='basic-navbar-nav'>
        //                 {/* <Nav class="mx-auto order-0">
        //                     //add something here like brand, category in future
        //                 </Nav> */}
        //                 <Nav className="ml-auto">
        //                     <Route render={({ history }) => <SearchBox history={history} />} />
        //                     {userInfo && userInfo.isAdmin ? (
        //                         <NavDropdown title='Manager' id='adminmenu'>
        //                             <LinkContainer to='/admin/userlist'>
        //                                 <NavDropdown.Item>  Users </NavDropdown.Item>
        //                             </LinkContainer>

        //                             <LinkContainer to='/admin/productlist'>
        //                                 <NavDropdown.Item> Products </NavDropdown.Item>
        //                             </LinkContainer>

        //                             <LinkContainer to='/admin/orderlist'>
        //                                 <NavDropdown.Item> Orders </NavDropdown.Item>
        //                             </LinkContainer>
        //                         </NavDropdown>
        //                     ) : (
        //                         <LinkContainer to='/cart'>
        //                             <Nav.Link><i className='fas fa-shopping-cart'></i> Cart</Nav.Link>
        //                         </LinkContainer>)
        //                     }

        //                     {userInfo ? (
        //                         <NavDropdown title={userInfo.name} id='username'>
        //                             <LinkContainer to='/profile'>
        //                                 <NavDropdown.Item>
        //                                     {/* <i className="fa fa-user fa-fw" /> */}
        //                                     {' '}Profile </NavDropdown.Item>
        //                             </LinkContainer>
        //                             <NavDropdown.Item onClick={logoutHandler}>
        //                                 {/* <i className="fa fa-sign-out fa-fw" /> */}
        //                                 Logout </NavDropdown.Item>
        //                         </NavDropdown>

        //                     )
        //                         : <LinkContainer to='/login'>
        //                             <Nav.Link><i className='fas fa-user'></i> Sign In</Nav.Link>
        //                         </LinkContainer>}
        //                 </Nav>
        //             </Navbar.Collapse>
        //         </Container>
        //     </Navbar >
        // </header >

        <header className="header--style-2">
        {/*====== Nav 1 ======*/}
        <nav className="primary-nav-wrapper">
            <div className="container">
                {/*====== Primary Nav ======*/}
                <div className="primary-nav">
                    {/*====== Main Logo ======*/}
                    <Link className="main-logo" to="/">
                        <img src="images/logo/logo-2.png" alt />
                    </Link>
                    {/*====== End - Main Logo ======*/}
                    {/*====== Search Form ======*/}
                    <form className="main-form">
                        <label htmlFor="main-search" />
                        <input className="input-text input-text--border-radius input-text--style-2" type="text" id="main-search" placeholder="Search" />
                        <button className="btn btn--icon fas fa-search main-search-button" type="submit" />
                    </form>
                    {/*====== End - Search Form ======*/}
                    {/*====== Dropdown Main plugin ======*/}
                    <div className="menu-init" id="navigation">
                        <button className="btn btn--icon toggle-button toggle-button--white fas fa-cogs" type="button" />
                        {/*====== Menu ======*/}
                        <div className="ah-lg-mode">
                            <span className="ah-close">â Close</span>
                            {/*====== List ======*/}
                            <ul className="ah-list ah-list--design1 ah-list--link-color-white">
                                <li className="has-dropdown" data-tooltip="tooltip" data-placement="left" title="Account">
                                    <a><i className="far fa-user-circle" /></a>
                                    {/*====== Dropdown ======*/}
                                    <span className="js-menu-toggle" />
                                    <ul style={{width: 120}}>
                                        <li>
                                            <a href="dashboard.html"><i className="fas fa-user-circle u-s-m-r-6" />
                                            <span>Account</span></a></li>
                                        <li>
                                            <a href="signup.html"><i className="fas fa-user-plus u-s-m-r-6" />
                                            <span>Signup</span></a></li>
                                        <li>
                                            <a href="signin.html"><i className="fas fa-lock u-s-m-r-6" />
                                            <span>Signin</span></a></li>
                                        <li>
                                            <a href="signup.html"><i className="fas fa-lock-open u-s-m-r-6" />
                                            <span>Signout</span></a></li>
                                    </ul>
                                    {/*====== End - Dropdown ======*/}
                                </li>
                                
                                <li data-tooltip="tooltip" data-placement="left" title="Contact">
                                    <a href="tel:+0900901904"><i className="fas fa-phone-volume" /></a></li>
                                <li data-tooltip="tooltip" data-placement="left" title="Mail">
                                    <a href="mailto:contact@domain.com"><i className="far fa-envelope" /></a></li>

                            </ul>
                            {/*====== End - List ======*/}
                        </div>
                        {/*====== End - Menu ======*/}
                    </div>
                    {/*====== End - Dropdown Main plugin ======*/}
                </div>
                {/*====== End - Primary Nav ======*/}
            </div>
        </nav>
        {/*====== End - Nav 1 ======*/}
        
        {/*====== Nav 2 ======*/}
        <nav className="secondary-nav-wrapper">
            <div className="container">
            {/*====== Secondary Nav ======*/}
            <div className="secondary-nav">
                {/*====== Dropdown Main plugin ======*/}
                <div className="menu-init" id="navigation1">
                </div>
                {/*====== End - Dropdown Main plugin ======*/}

                {/*====== Dropdown Main plugin ======*/}
                <div className="menu-init" id="navigation2">
                <button className="btn btn--icon toggle-button toggle-button--white fas fa-cog" type="button" />
                {/*====== Menu ======*/}
                <div className="ah-lg-mode">
                    <span className="ah-close">â Close</span>
                    {/*====== List ======*/}
                    <ul className="ah-list ah-list--design2 ah-list--link-color-white">
                        <li>
                            <a href="shop-side-version-2.html">ALL PRODUCTS</a>
                        </li>
                        <li className="has-dropdown">
