import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Button, Form, Image, Row, Col, Toast } from 'react-bootstrap-v5'
import Message from '../components/Message'
import Loader from '../components/Loader'
import FormContainer from '../components/FormContainer'
import { createBrand } from '../actions/brandActions'
import { BRAND_CREATE_RESET } from '../constants/brandConstants'

const BrandCreateScreen = ({ history }) => {
    const [name, setName] = useState('')
    const [description, setDescription] = useState('')
    const [message, seMessage] = useState(null)

    const dispatch = useDispatch()

    const brandCreate = useSelector(state => state.brandCreate)
    const { loading: loadingCreate, error: errorCreate, success: successCreate } = brandCreate

    useEffect(() => {
        if (successCreate) {
            dispatch({ type: BRAND_CREATE_RESET })
            history.push('/admin/brandlist')
        }
    }, [dispatch, history, successCreate])

    const submitHandler = (e) => {
        e.preventDefault()
        if (name.trim() === "") {
            setMessage('Name must not be empty !')
        }
        else if (description.trim() === "") {
            setMessage('Description must not be empty !')
        }
        else {
            dispatch(createBrand(name, description))
        }
    }

    

    return (
        <>

            <Link to='/admin/brandlist' className='btn btn-light my-3'> Go Back </Link>
            <FormContainer>
                <h1>Create Brand</h1>
                {loadingCreate ? <Loader />
                    : errorCreate ? <Message variant='danger'>{errorCreate}</Message>
                        : (
                            <>
                                {message && <Message variant='danger'> {message} </Message>}
                                <Form onSubmit={submitHandler}>
                                    <Form.Group controlId='name'>
                                        <Form.Label>Name</Form.Label>
                                        <Form.Control type='name' placeholder='Enter name' value={name}
                                            onChange={(e) => setName(e.target.value)}></Form.Control>
                                    </Form.Group>

                                    <Form.Group controlId='description'>
                                        <Form.Label>Description</Form.Label>
                                        <Form.Control type='text' placeholder='Enter description' value={description}
                                            onChange={(e) => setDescription(e.target.value)}></Form.Control>
                                    </Form.Group>

                                    <Form.Group controlId='image1'>
                                        <Form.Label>Image From Left</Form.Label>
                                        {!images[0] ?
                                            (
                                                <>
                                                    <Form.Control type='text' placeholder='Enter image URL' value={images}
                                                        onChange={(e) => setImages(e.target.value)}></Form.Control>
                                                    <Form.File id='image-file'
                                                        // label='Choose Image'
                                                        custom
                                                        onChange={uploadFileHandler}>

                                                    </Form.File>
                                                </>
                                            ) : (
                                                <Image src={images[0].url} width="120" height="120" style={{ marginLeft: "20px" }} />
                                            )}
                                        {uploading && <Loader />}
                                    </Form.Group>
                                    <br></br>
                                    <Button className="btn btn-primary" type='submit' variant='primary'
                                    >
                                        Create
                                    </Button>
                                </Form>
                            </>
                        )}
            </FormContainer>

        </>
    )

}

export default BrandCreateScreen
