import { GET_STATISTIC_PRODUCT_SOLD_FAIL, GET_STATISTIC_PRODUCT_SOLD_REQUEST, GET_STATISTIC_PRODUCT_SOLD_SUCCESS } from '../constants/statisticConstants'

export const statisticProductSoldListReducer = (state = { data: [] }, action) => {
    switch (action.type) {
        case GET_STATISTIC_PRODUCT_SOLD_REQUEST:
            return { loading: true, data: [] }
        case GET_STATISTIC_PRODUCT_SOLD_SUCCESS:
            return {
                loading: false,
                statisticProductSolds: action.payload,
                
            }
        case GET_STATISTIC_PRODUCT_SOLD_FAIL:
            return { loading: false, error: action.payload }
        default:
            return state
    }
}
