import React, { useEffect } from "react";
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import { Bar } from 'react-chartjs-2';
import { listStatisticProductSold } from '../actions/statisticActions'

const DashboardScreen = ({ }) => {
    const dispatch = useDispatch()

    const statisticProductSoldList = useSelector(state => state.statisticProductSoldList)
    const { loading, error, data: ProductSoldList } = statisticProductSoldList

    const names = ProductSoldList && ProductSoldList.map((item) => (item.name.slice(0, 20)));
    const solds = ProductSoldList && ProductSoldList.map((item) => (item.sold));

    useEffect(() => {
        if (!ProductSoldList) {
            dispatch(listStatisticProductSold())
        }
    }, [dispatch])
    return (
        <>
         {
                loading ? <Loader />
                    : error ? <Message variant='danger'> {error} </Message>
                        : (
                            <>
                                <Row>
                                    {products.map((product) => (
                                        <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                                            <Product product={product} />
                                        </Col>
                                    ))}
                                </Row>
                                <div class="section-title section-title-center"><b></b><Link to="/allproduct">See All Products</Link><b></b></div>
                            </>
                        )

            }
            <Bar
                data={{
                    labels: names,
                    datasets: [
                        {
                            label: "Sold",
                            backgroundColor: [
                                'rgba(255, 99, 132, 0.2)',
                                'rgba(54, 162, 235, 0.2)',
                                'rgba(255, 206, 86, 0.2)',
                                'rgba(75, 192, 192, 0.2)',
                                'rgba(153, 102, 255, 0.2)',
                                'rgba(255, 159, 64, 0.2)'
                            ],
                            borderColor: [
                                'rgba(255, 99, 132, 1)',
                                'rgba(54, 162, 235, 1)',
                                'rgba(255, 206, 86, 1)',
                                'rgba(75, 192, 192, 1)',
                                'rgba(153, 102, 255, 1)',
                                'rgba(255, 159, 64, 1)'
                            ],
                            borderWidth: 1,
                            data: solds
                        }
                    ]
                }}
                options={{
                    legend: { display: false },
                    title: {
                        display: true,
                        text: "Alooo aloooo"
                    }
                }}
            >

            </Bar>
        </>
    )
}
export default DashboardScreen;
