import React from 'react'
import { Card } from 'react-bootstrap'
import Rating from '../components/Rating'
import { Link } from 'react-router-dom'

const Product = ({ product }) => {
    return (
        <Card className='my-3 p-3 rounded'>
            //m hiểu chưa
            //cái product.images là mảng nên để kiểu dưới kia, lấy th đầu tiên
            //m add bằng postman thì add 1 array cũng được, r từ từ làm hiển thị kiểu kia sau, lấy th đầu tiên trước đã
            // má chơi create sample :v
            <Link to={`/product/${product._id}`}>
                <Card.Img src={product.images[0].url} className='photo' variant='top'></Card.Img>
            </Link>

            <Card.Body>
                <Link to={`/product/${product._id}`} style={{ textDecoration: 'none' }} >
                    <Card.Title as='div'  >
                        <strong> {product.name}</strong>
                    </Card.Title>
                </Link>
                <Card.Text as='div'>
                    <Rating
                        value={product.rating}
                        text={`${product.numReviews} reviews`}
                    />
                </Card.Text>

                <Card.Text as='h3'>
                    ${product.price}
                </Card.Text>
            </Card.Body>
        </Card >
    )
}

export default Product
