import axios from 'axios'
import { GET_STATISTIC_PRODUCT_SOLD_FAIL, GET_STATISTIC_PRODUCT_SOLD_REQUEST, GET_STATISTIC_PRODUCT_SOLD_SUCCESS } from '../constants/statisticConstants'

export const listStatisticProductSold = () => async (dispatch, getState) => {
    try {
        dispatch({ type: GET_STATISTIC_PRODUCT_SOLD_REQUEST })

        const {
            userLogin: { userInfo },
        } = getState()

        const config = {
            headers: {
                Authorization: `Bearer ${userInfo.token}`
            },
        }

        const data = await axios.get(`/api/statistic`, config)
        dispatch({
            type: GET_STATISTIC_PRODUCT_SOLD_SUCCESS,
            payload: data
        })
    } catch (error) {
        dispatch({
            type: GET_STATISTIC_PRODUCT_SOLD_FAIL,
            payload: error.response && error.response.data.message
                ? error.response.data.message
                : error.message
        })
    }
}
