import React from "react";
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import { Bar } from 'react-chartjs-2';
import { listStatisticProductSold } from '../actions/statisticActions'

const DashboardScreen = ({ }) => {
    const dispatch = useDispatch()

    const statisticProductSoldList = useSelector(state => state.statisticProductSoldList)
    const { loading, error, data } = statisticProductSoldList

    const names = data && data.map(item => item.name.slice(0, 20));
    const solds = data && data.map(item => item.sold);

    useEffect(() => {
        dispatch(listStatisticProductSold())
    }, [dispatch])
    return (
        <>
            <Bar
                data={{
                    labels: names,
                    datasets: [
                        {
                            label: "Sold",
                            backgroundColor:[
                                
                            ]
                        }
                    ]
                }}>

            </Bar>
        </>
    )
}
export default DashboardScreen;
