import { createStore, combineReducers, applyMiddleware } from 'redux'
import thunk from 'redux-thunk'
import { composeWithDevTools } from 'redux-devtools-extension'
import { producListReducer, producDetailsReducer } from './reducers/productReducers'
import { cartReducer } from './reducers/cartReducers'
import {
    userLoginReducer,
    userRegisterReducer,
    userDetailReducer,
    userUpdateProfileReducer
} from './reducers/userReducers'

const reducer = combineReducers({
    productList: producListReducer,
    productDetails: producDetailsReducer,
    cart: cartReducer,
    userLogin: userLoginReducer,
    userRegister: userRegisterReducer,
    userDetail: userDetailReducer,
    userUpdateProfile: userUpdateProfileReducer
})

const cartItemsFromStorage =
    localStorage.getItem('cartItems') ?
        JSON.parse(localStorage.getItem('cartItems')) 
        : []

const userInfoFromStorage =
    localStorage.getItem('userInfo')
        ? JSON.parse(localStorage.getItem('userInfo'))
        : null

const shippingAddressFromStorage =
    localStorage.getItem('shippingAddress') ?JSON.parse(localStorage.getItem('shippingAddress')) 
        : {}

const initialState = {
    cart: {
        cartItems: cartItemsFromStorage,
        shippingAddress: shippingAddressFromStorage
    },
    userLogin: { userInfo: userInfoFromStorage }
}

const middleware = [thunk]

const store = createStore(reducer, initialState,
    composeWithDevTools(applyMiddleware(...middleware)))

export default store
